import { Toolbar, IconButton, Typography, useMediaQuery } from "@mui/material";
import React, { useEffect, useState } from "react";
import { Box } from "@mui/system";
import DrawerComponent from "./DrawerComponent";
import { useTranslation } from "react-i18next";
import { useTheme } from "@mui/material/styles";
import ListIcon from "@mui/icons-material/List";
import { useAppSelector, useAppDispatch } from "../../store/hooks";
import { useNavigate } from "react-router-dom";
import { usersActions } from "../../store/userSlice";
import SpeedDial from "@mui/material/SpeedDial";
import SpeedDialIcon from "@mui/material/SpeedDialIcon";
import SpeedDialAction from "@mui/material/SpeedDialAction";
import FileCopyIcon from "@mui/icons-material/FileCopyOutlined";
import SaveIcon from "@mui/icons-material/Save";
import PrintIcon from "@mui/icons-material/Print";
import ShareIcon from "@mui/icons-material/Share";

const Navbar = () => {
  const [openDrawer, setOpenDrawer] = useState(true);
  const theme = useTheme();
  const matches = useMediaQuery(theme.breakpoints.down("sm"));
  const reduxIsLoggedIn = useAppSelector((state) => state.isLoggedIn);
  let navigate = useNavigate();
  const reduxUser: string = useAppSelector((state) => state.user);
  const storedLan: string = localStorage.getItem("lan") as string;
  const dispatch = useAppDispatch();
  const reduxLan: string = useAppSelector((state) => state.lan);
  const { t, i18n } = useTranslation("main");
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const actions = [
    { icon: <FileCopyIcon />, name: "Copy" },
    { icon: <SaveIcon />, name: "Save" },
    { icon: <PrintIcon />, name: "Print" },
    { icon: <ShareIcon />, name: "Share" },
  ];

  const LanguageChangeHandler = (e: React.ChangeEvent<HTMLSelectElement>) => {
    console.log(e.target.value);
    i18n.changeLanguage(e.target.value);
  };

  const logoutHandler = () => {
    dispatch(usersActions.updateIdToken(""));
    dispatch(usersActions.updateIsLoggedIn(false));
    dispatch(usersActions.updateUser(""));
    localStorage.removeItem("token");
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <Box
        sx={{
          backgroundColor:
            "linear-gradient(to left, #3399ff 27%, #9999ff 100%);",
        }}
      >
        <Toolbar>
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              width: "100%",
              alignItems: "center",
              padding: "10px 0px",
            }}
            component="div"
          >
            {/* link */}
            {matches && (
              <DrawerComponent
                openDrawer={openDrawer}
                setOpenDrawer={setOpenDrawer}
              />
            )}
            {reduxIsLoggedIn && !matches ? (
              <Box
                sx={{
                  display: "flex",
                }}
              >
                <Typography
                  sx={{
                    marginRight: "20px",
                    cursor: "pointer",
                    color: "white",
                  }}
                  onClick={() => {
                    if (reduxUser === "Hans" || reduxUser === "Sarah") {
                      navigate("/hompageen");
                    } else if (
                      reduxUser === "성재" ||
                      reduxUser === "엄마" ||
                      reduxUser === "아빠" ||
                      reduxUser === "은지"
                    ) {
                      navigate("/hompagekr");
                    }
                  }}
                >
                  {reduxLan === "한국어" ? "홈" : "Home"}
                </Typography>
                <Typography
                  sx={{
                    marginRight: "20px",
                    cursor: "pointer",
                    color: "white",
                  }}
                  onClick={() => {
                    navigate("/changepassword");
                  }}
                >
                  {reduxLan === "한국어"
                    ? "비밀번호 바꾸기"
                    : "Change Password"}
                </Typography>
                <Typography
                  sx={{
                    marginRight: "20px",
                    cursor: "pointer",
                    color: "white",
                  }}
                  onClick={() => {
                    if (reduxUser === "Hans" || reduxUser === "Sarah") {
                      navigate("/msglogen");
                    } else if (
                      reduxUser === "성재" ||
                      reduxUser === "엄마" ||
                      reduxUser === "아빠" ||
                      reduxUser === "은지"
                    ) {
                      navigate("/msglogkr");
                    }
                  }}
                >
                  {reduxLan === "한국어" ? "메세지 로그" : "Message Log"}
                </Typography>
                <select
                  className="nav-link bg-dark border-0 ml-1 mr-2 mt-1"
                  style={{ float: "right", marginRight: "20px" }}
                  onChange={LanguageChangeHandler}
                >
                  <option value="kr">한국어</option>
                  <option value="en">English</option>
                </select>
              </Box>
            ) : null}
            {/* Button link */}
            <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              {matches ? (
                <IconButton onClick={() => setOpenDrawer(true)}>
                  <ListIcon></ListIcon>
                </IconButton>
              ) : !reduxIsLoggedIn ? (
                <>
                  <button
                    onClick={() => navigate("/login")}
                    style={{ marginRight: "20px" }}
                  >
                    {reduxLan === "한국어" ? "로그인" : "LogIn"}
                  </button>
                  <button onClick={() => navigate("/signin")}>
                    {reduxLan === "한국어" ? "계정 생성" : "Register Account"}
                  </button>
                  <select
                    className="nav-link bg-dark border-0 ml-1 mr-2 mt-1"
                    style={{ float: "right", marginRight: "20px" }}
                    onChange={LanguageChangeHandler}
                  >
                    <option value="kr">한국어</option>
                    <option value="en">English</option>
                  </select>
                </>
              ) : (
                // <button onClick={() => navigate("/updateusername")}>
                //   {reduxLan === "한국어"
                //     ? "ㄹ"
                //     : "Change Display name"}
                // </button>
                <button onClick={logoutHandler}>
                  {reduxLan === "한국어" ? "로그아웃" : "Logout"}
                </button>
              )}
            </Box>
          </Box>
        </Toolbar>
      </Box>
      <Box sx={{ height: 330, transform: "translateZ(0px)", flexGrow: 1 }}>
        <SpeedDial
          ariaLabel="SpeedDial tooltip example"
          sx={{ position: "absolute", bottom: 16, right: 16 }}
          icon={<SpeedDialIcon />}
          onClose={handleClose}
          onOpen={handleOpen}
          open={open}
        >
          {actions.map((action) => (
            <SpeedDialAction
              key={action.name}
              icon={action.icon}
              tooltipTitle={action.name}
              tooltipOpen
              onClick={handleClose}
            />
          ))}
        </SpeedDial>
      </Box>
    </>
  );
};

export default Navbar;
