import MainNavigation from "./MainNavigation";
import React, { useState } from "react";
import Box from "@mui/material/Box";
import SpeedDial from "@mui/material/SpeedDial";
import SpeedDialIcon from "@mui/material/SpeedDialIcon";
import SpeedDialAction from "@mui/material/SpeedDialAction";
import LoginIcon from "@mui/icons-material/Login";
import LogoutIcon from "@mui/icons-material/Logout";
import AddToHomeScreenIcon from "@mui/icons-material/AddToHomeScreen";
import VpnKeyIcon from "@mui/icons-material/VpnKey";
import { useAppSelector, useAppDispatch } from "../../store/hooks";
import MessageIcon from "@mui/icons-material/Message";
import HomeIcon from "@mui/icons-material/Home";
import { usersActions } from "../../store/userSlice";
import { useNavigate } from "react-router-dom";

const Layout = (props: { children: React.ReactFragment }) => {
  const [open, setOpen] = useState(false);
  const actions = [
    { icon: <LoginIcon />, name: "LogIn" },
    { icon: <LogoutIcon />, name: "LogOut" },
    { icon: <AddToHomeScreenIcon />, name: "SignIn" },
    { icon: <VpnKeyIcon />, name: "Change Password" },
    { icon: <MessageIcon />, name: "Message Log" },
    { icon: <MessageIcon />, name: "메세지 로그" },
    { icon: <HomeIcon />, name: "Home" },
    { icon: <HomeIcon />, name: "홈" },
  ];
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const dispatch = useAppDispatch();
  let navigate = useNavigate();

  const logoutHandler = () => {
    dispatch(usersActions.updateIdToken(""));
    dispatch(usersActions.updateIsLoggedIn(false));
    dispatch(usersActions.updateUser(""));
    localStorage.removeItem("token");
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <MainNavigation />
      <main>{props.children}</main>
      <Box sx={{ height: 440, transform: "translateZ(0px)", flexGrow: 1 }}>
        <SpeedDial
          ariaLabel="SpeedDial tooltip example"
          sx={{
            position: "absolute",
            bottom: -34,
            right: 20,
          }}
          icon={<SpeedDialIcon />}
          onClose={handleClose}
          onOpen={handleOpen}
          open={open}
        >
          {actions.map((action) => (
            <SpeedDialAction
              key={action.name}
              icon={action.icon}
              tooltipTitle={action.name}
              tooltipOpen
              onClick={() => {
                console.log(action.name);
                handleClose();
              }}
            />
          ))}
        </SpeedDial>
      </Box>
    </>
  );
};

export default Layout;
